mapper.select:
select id,questionId,userId,supportSum,commentSum,time
from (
    select id,questionId,userId,supportSum,commentSum,time from answer
    where questionId = #{questionId} and supportSum >= #{suppportSum}
) as a
where id < #{id} limit 0,1

select id,questionId,userId,supportSum,commentSum,time
from (
    select id,questionId,userId,supportSum,commentSum,time from answer
    where questionId = #{questionId} and supportSum < #{supportSum} order by supportSum desc,id asc
) as a
where id >= #{id} limit 0,3
